import React, { Component } from "react";
import { Input, Button, Modal } from "antd";

export default class SeachBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visible: false,
    };
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = (e) => {
    this.setState({
      visible: false,
    });
  };

  handleCancel = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };
  render() {
    return (
      <div style={{ padding: 30, margin: 15, display: "flex" }}>
        <Input
          onChange={props.handleChange}
          onPressEnter={this.props.handleSubmit}
          onSearch={this.props.handleSubmit}
          size="middle"
          type="text"
        />
        <Button type="primary" onClick={this.showModal}>
          Save
        </Button>
        <Modal
          title="Сохранить запросы"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
          <input value={this.props.input} />
        </Modal>
      </div>
    );
  }
}
